package com.mikankui.hanasite.kakeibo;import java.util.List;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.view.View.OnClickListener;import android.widget.ArrayAdapter;import android.widget.Button;import android.widget.TextView;import android.widget.Toast;public class MonthListMemberAdapter extends ArrayAdapter<RowItem> {	private LayoutInflater mInflater;	private TextView rId;	private TextView rValue;	private Button mButton;	private String dayStr;	public MonthListMemberAdapter(Context context, List<RowItem> item) {		super(context, 0, item);		dayStr = context.getString(R.string.day);		mInflater = (LayoutInflater) context				.getSystemService(Context.LAYOUT_INFLATER_SERVICE);	}	public View getView(final int position, View convertView, ViewGroup parent) {		if (convertView == null) {			convertView = mInflater.inflate(R.layout.main_listitem, null);		}		final RowItem item = this.getItem(position);		if (item != null) {			rId = (TextView) convertView.findViewById(R.id.rowId);			rId.setText(item.getRowid());			rValue = (TextView) convertView.findViewById(R.id.rowValue);			rValue.setText(item.getRowValue());			mButton = (Button) convertView.findViewById(R.id.rowAction);			mButton.setOnClickListener(new OnClickListener() {				public void onClick(View v) {					TargetDate.setDay(Integer.parseInt(item.getRowid()							.replaceAll(dayStr, "")));					Toast.makeText(getContext(), TargetDate.check(),							Toast.LENGTH_SHORT).show();					Intent intent = new Intent();					intent.setClass(getContext(), DayListViewActivity.class);					intent.setAction(Intent.ACTION_VIEW);					getContext().startActivity(intent);					((Activity) getContext()).overridePendingTransition(							R.anim.push_left_in, R.anim.push_left_out);				}			});		}		return convertView;	}}