package com.mikankui.hanasite.kakeibo;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;public class PreferenceActivity extends Activity implements OnClickListener{	Button back;		protected void onCreate(Bundle savedInstanceState) { 		 super.onCreate(savedInstanceState); 		 setContentView(R.layout.preference); 		 		 back = (Button) findViewById(R.id.back);		 back.setOnClickListener(this);	}	@Override	public void onClick(View v) {		switch (v.getId()) {		case R.id.back:			Intent intent = new Intent(this, YearListViewActivity.class);			intent.setAction(Intent.ACTION_VIEW);			startActivity(intent);			break;		}			}}