package com.mikankui.hanasite.kakeibo;import java.text.SimpleDateFormat;import java.util.Calendar;public class TargetDate {	private static TargetDate thisInstance = new TargetDate();	private static Calendar cal = Calendar.getInstance();	private static SimpleDateFormat sdf = new SimpleDateFormat(			"yyyy-MM-dd HH:mm:ss");	private TargetDate() {	}	public TargetDate getInstanse() {		return thisInstance;	}	public static int getYear() {		return cal.get(Calendar.YEAR);	}	public static int getMonth() {		return cal.get(Calendar.MONTH) + 1;	}	public static int getDay() {		return cal.get(Calendar.DAY_OF_MONTH);	}	public static void setYear(int year) {		cal.set(Calendar.YEAR, year);	}	public static void setMonth(int month) {		cal.set(Calendar.MONTH, month);	}	public static void setDay(int day) {		cal.set(Calendar.DAY_OF_MONTH, day);	}	public static void nextYear() {		cal.add(Calendar.YEAR, 1);	}	public static void nextMonth() {		cal.add(Calendar.MONTH, 1);	}	public static void nextDay() {		cal.add(Calendar.DAY_OF_MONTH, 1);	}	public static void backYear() {		cal.add(Calendar.YEAR, -1);	}	public static void backMonth() {		cal.add(Calendar.MONTH, -1);	}	public static void backDay() {		cal.add(Calendar.DAY_OF_MONTH, -1);	}	public static String check() {		return sdf.format(cal.getTime());	}	public static int getActualMaximumOfMonth() {		return cal.getActualMaximum(Calendar.DATE);	}}