package com.mikankui.hanasite.kakeibo.input;import java.util.ArrayList;import java.util.List;import com.mikankui.hanasite.kakeibo.YearListViewActivity;import android.app.Activity;import android.content.Intent;import android.os.Bundle;public class InputActivity extends Activity {	private ArrayList<String> splitter;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setSplitter(" ", "ã€€");		if (Intent.ACTION_SEND_MULTIPLE.equals(getIntent().getAction())) {			UpdateDbByLine udbl = new UpdateDbByLine();			udbl.updateDb(getIntent(), splitter);			Intent intent = new Intent(this, YearListViewActivity.class);			intent.setAction(Intent.ACTION_VIEW);			startActivity(intent);		} else {			Intent intent = new Intent(this, YearListViewActivity.class);			intent.setAction(Intent.ACTION_VIEW);			startActivity(intent);		}	}	/**	 * @param splitter	 */	public List<String> setSplitter(String... splitter) {		this.splitter = new ArrayList<String>();		for (String split : splitter) {			this.splitter.add(split);		}		return this.splitter;	}}