package com.mikankui.hanasite.kakeibo.util;import java.util.List;import com.mikankui.hanasite.kakeibo.preference.PreferenceData;public class SpliterStringTransfar {	private final static String splitstr = "SPRITSTR";	private static PreferenceData pd;		/*	 * 分割文字列により行を分割する	 * はじめにセパレートとしてListに登録された文字列を全て"SPLITSTR"に置き換え、	 * その後一気に"SPRITSTRで分割する"	 */	public static String[] getSplitString(String target){		return target.split(splitstr);	}		public static String getSplitstr() {		return splitstr;	}		public static String makeSplitStirng(String[] splitStrings){		String splitStringsLine = "";		for(String s:splitStrings){			splitStringsLine = splitStringsLine +s+ splitstr;		}		return splitStringsLine;	}}